import React from "react"
import {nanoid} from 'nanoid'

export default function Question (props) {
	const [answers, setAnswers] = React.useState([])

	function createAnswersTab(question) {
		const answersTab = question.incorrect_answers
		answersTab.push(question.correct_answer)
		setAnswers(answersTab.map((elem) => ({
			id : nanoid(),
			answer : elem,
			isSelected : false 
		})))

	}

	React.useEffect(() => {
		props.question.forEach(() => createAnswersTab(question))
	})

	function selectAnswer(id)
	{
		setAnswers((prevAnswers) => (prevAnswers.map((answer) => {
			return answer.id === id ?
				{ ...answer, isSelected : true} :
				{ ...answer, isSelected : false}
		})))
	}

	const style  = {
		backgroundColor : "#293264"
	}

	const answersElements = props.answers.map( (elem) =>
		<div style={elem.isSelected ? style : {}} 
		onClick={() => props.selectAnswer(elem.id)} 
		className="answers-button" key={elem.id}>
			{elem.answer}
		</div>
	)
	console.log(props.question.question, props.answers)
	return (
		<div className="question-container">
			<h2 className="question-title" >{props.question.question}</h2>
			<div className="answers-container">{answersElements}</div>
		</div>


	)
}
