import React from "react"
import {nanoid} from 'nanoid'

export default function Question (props) {
	function selectAnswer(id)
	{
		setAnswers((prevAnswers) => (prevAnswers.map((answer) => {
			return answer.id === id ?
				{ ...answer, isSelected : true} :
				{ ...answer, isSelected : false}
		})))
	}

	const style  = {
		backgroundColor : "#D6DBF5"
	}

	const answersElements = answers.map( (elem) =>
		<div style={elem.isSelected ? style : {}} 
		onClick={() => selectAnswer(elem.id)} 
		className="answers-button" key={elem.id}>
			{elem.answer}
		</div>
	)
	return (
		<div className="question-container">
			<h2 className="question-title" >{props.question.question}</h2>
			<div className="answers-container">{answersElements}</div>
		</div>


	)
}
