import './App.css';
import React from "react"
import Question from './components/Question';

function App() 
{
	const [questions, setQuestions] = React.useState([])
	// const [answers, setAnswers] = React.useState([])

	async function generateQuestions()
	{
		const res = await fetch("https://opentdb.com/api.php?amount=5")
		const data = await res.json()
		console.log(data.result)
		setQuestions(data.results)
	}

	function createAnswersTab (question) {
		console.log(question)
		const incorrect_answers = question.incorrect_answers
		console.log(incorrect_answers)
		const answers = incorrect_answers.push(question.correct_answers)
		// const shuffleAnswers = answers.sort((a, b) => 0.5 - Math.random())
		return answers
	}

	const questionsElements = questions.map(question =>
		<Question key={question.question} question={question} 
		answers={createAnswersTab(question)}></Question>
		)
	return (
		<div className="App">
		{
			questions.length === 0 ?
				<div className='home-page'>
					<h1 className='home-title'>Quizzical</h1>
					<p className='home-descrption'>Some description if needed</p>
					<button onClick={generateQuestions} className='button-start'>Start quiz</button>
				</div> :
				questionsElements

		}
			<div>

			</div>
		</div>
	);
}

export default App;
