import React from "react"

export default function Question (props) {
	const answers = props.question.incorrect_answers
	console.log("incorect", answers)
	answers.push(props.question.correct_answer)
	console.log("corect", answers)
	const shuffleAnswers = answers.sort((a, b) => 0.5 - Math.random())
	console.log("corect", shuffleAnswers)
	const answersElements = shuffleAnswers.map( (elem) =>
		<div key={props.question.correct_answer}>{elem}</div>
	)
	return (
		<div>
			<h2>{props.question.question}</h2>
			{answersElements}
		</div>


	)
}
