import './App.css';
import React from "react"
import Question from './components/Question';
import { nanoid } from 'nanoid'
function App() 
{
	const [questions, setQuestions] = React.useState([])
	const [questionsElements, setQuestionsElements] = React.useState([])
	const [answers, setAnswers] = React.useState([])
	async function generateQuestions()
	{
		const res = await fetch("https://opentdb.com/api.php?amount=5&type=multiple")
		const data = await res.json()
		console.log(data.results)
		setQuestions(data.results)
	}

	function createAnswersTab (question) {
		const answers = question.incorrect_answers
		answers.push(question.correct_answer)
		setAnswers(answers.map((elem) => ({
			id : nanoid(),
			answer : elem,
			isSelected : false 
		})))
	}

	function selectAnswer (id)
	{
		setAnswers((prevAnswers) => (prevAnswers.map((answer) => {
			return answer.id === id ?
				{ ...answer, isSelected : true} :
				{ ...answer, isSelected : false}
		})))
	}

	React.useEffect(() => {
		setQuestionsElements(questions.map(question =>
			<Question key={question.question} question={question} 
			answers={createAnswersTab(question)} handleClick={selectAnswer}></Question>
			))
	}, [questions])
	return (
		<div className="App">
		{
			questions.length === 0 ?
				<div className='home-page'>
					<h1 className='home-title'>Quizzical</h1>
					<p className='home-descrption'>Some description if needed</p>
					<button onClick={generateQuestions} className='button-start'>Start quiz</button>
				</div> :
				<div className='quizz-page'>
					{questionsElements}
				</div>

		}
			<div>

			</div>
		</div>
	);
}

export default App;
