import './App.css';
import React from "react"
import Question from './components/Question';

function App() 
{
	const [questions, setQuestions] = React.useState([])
	const [answers, setAnswers] = React.useState([])

	async function generateQuestions()
	{
		const res = await fetch("https://opentdb.com/api.php?amount=5")
		const data = await res.json()
		setQuestions(data.results)
		setAnswers(data.results.incorrect_answers.push(data.results.correct_answer))
	}
	console.log(questions)
	const questionsElements = questions.map(question =>
		<Question key={question.question} question={question} answers={answers}></Question>
		)
	return (
		<div className="App">
		{
			questions.length === 0 ?
				<div className='home-page'>
					<h1 className='home-title'>Quizzical</h1>
					<p className='home-descrption'>Some description if needed</p>
					<button onClick={generateQuestions} className='button-start'>Start quiz</button>
				</div> :
				questionsElements

		}
			<div>

			</div>
		</div>
	);
}

export default App;
